var script=document.createElement('script');script.type='text/javascript';script.textContent='('+(function(){var friends={};var lastSent={};var canNextSend=null;var logTypes={"friendDrop":"Friends","jobDrop":"Jobs","battleDrop":"Fort Battles","adminDrop":"Admin intervention","wofPay":"Used","duelDrop":"Duels","duelNPCDrop":"NPC Duels","adventureDrop":"Adventures","questDrop":"Quests","itemUse":"Used items","buildDrop":"Construction"};var newLogs=true;var logsMetadata=null;var playerLogs=null;var dropTypeLogs=null;var logsLocked=false;var sortingIsAscent=true;function getActiveSesKeys(){return Object.keys(Game.sesData);}
function timeUntilSesReady(friendId){if(!lastSent.hasOwnProperty(friendId)){return 0;}
var yesterday=Date.now()/ 1000-3600*23;return Math.max(0,Math.floor(lastSent[friendId]-yesterday));}
function getSesReadyCount(){var count=0;$.each(friends,function(playerId,client){if(timeUntilSesReady(playerId)===0){count++;}});return count;}
function getFriendCount(){return Object.keys(friends).length;}
function getFriendsList(){return new Promise(function(resolve,reject){Ajax.remoteCallMode('friendsbar','search',{search_type:'friends'},function(data){if(data.error){return reject(data.msg);}
$.each(data.players,function(i,client){if(client.player_id!==Character.playerId){friends[client.player_id]=west.storage.FriendsBar.prototype.normalizeAvatars_(client,i);delete friends[client.player_id].experience;delete friends[client.player_id].x;delete friends[client.player_id].y;}});var sesKey=getActiveSesKeys()[0];$.each(data.eventActivations,function(i,eventActivation){if(eventActivation.event_name===sesKey){lastSent[eventActivation.friend_id]=eventActivation.activation_time;}});return resolve(data.msg);});});}
function getSesSmallestTimer(){if(canNextSend!=null)
return Math.max(0,Math.floor(canNextSend-Date.now()/ 1000));var count=0;var smallestTimer=0;$.each(friends,function(playerId,client){if(count==0)
smallestTimer=timeUntilSesReady(playerId);if(timeUntilSesReady(playerId)<smallestTimer)
smallestTimer=timeUntilSesReady(playerId);count++;});canNextSend=Date.now()/ 1000+smallestTimer;return smallestTimer;}
function sendSesCurrency(friendId){return new Promise(function(resolve,reject){Ajax.remoteCall('friendsbar','event',{player_id:friendId,event:getActiveSesKeys()[0]},function(data){if(data.error){return reject(data.msg);}
lastSent[friendId]=data.activationTime;canNextSend=null;setTimeout(function(){updateCounter();updateCounterTimer();},1000);return resolve(data.msg);});});}
function processLogs(background){if(logsLocked)throw new Error("Please don't try and process the logs twice at the same time.");if(!newLogs)return Promise.resolve();logsLocked=true;loadLogs();var sesKey=getActiveSesKeys()[0];if(typeof(logsMetadata.logTypes)=='undefined')
logsMetadata.logTypes={};if(sesKey!==logsMetadata.sesKey){playerLogs={};dropTypeLogs={};logsMetadata.sesKey=sesKey;}
return new Promise(function(resolve,reject){var generator=processLogsBatches(sesKey,background,resolve,reject);generator.next();generator.next(()=>generator.next());});}
function*processLogsBatches(sesKey,background,resolve,reject){var callback=yield;var stats={newest:logsMetadata.newestSeen||0,hasNext:true};var page=1;do{yield processLogBatch(sesKey,page++,stats,callback,background);}while(stats.hasNext);logsMetadata.newestSeen=stats.newest;saveLogs();newLogs=false;logsLocked=false;return resolve();}
function processLogBatch(sesKey,page,stats,callback,background){Ajax.remoteCallMode('ses','log',{ses_id:sesKey,page:page,limit:100},function(data){if(data.error){logsLocked=false;return reject(data.msg);}
stats.hasNext=!data.entries.some(function(entry,i){if(entry.date<=logsMetadata.newestSeen){return true;}else if(i===0&&entry.date>stats.newest){stats.newest=entry.date;}
if(entry.details=="deduct"){entry.value*=-1;}
dropTypeLogs[entry.type]=(dropTypeLogs[entry.type]||0)+ +entry.value;if(typeof(logsMetadata.logTypes[entry.type])=='undefined'){logsMetadata.logTypes[entry.type]=entry.description;}
if(entry.type==='friendDrop'){var senderId=JSON.parse(entry.details).player_id;if(playerLogs.hasOwnProperty(senderId)){playerLogs[senderId].total+=+entry.value;playerLogs[senderId].frequency.push(entry.date);}else{playerLogs[senderId]={total:+entry.value,frequency:[entry.date]};}}})&&data.hasNext;if(background){setTimeout(callback,1000);}else{callback();}});}
function loadLogs(){logsMetadata=JSON.parse(localStorage.getItem('xshteff.betterfriends.logsMetadata'))||{};playerLogs=JSON.parse(localStorage.getItem('xshteff.betterfriends.playerLogs'))||{};dropTypeLogs=JSON.parse(localStorage.getItem('xshteff.betterfriends.dropTypeLogs'))||{};}
function saveLogs(){var prefix='xshteff.betterfriends.';localStorage.setItem(prefix+'logsMetadata',JSON.stringify(logsMetadata));localStorage.setItem(prefix+'playerLogs',JSON.stringify(playerLogs));localStorage.setItem(prefix+'dropTypeLogs',JSON.stringify(dropTypeLogs));}
function initialiseScript(){var sesKeys=getActiveSesKeys();if(sesKeys.length===0)return;registerToWestApi();getFriendsList().then(function(){getSesReadyCount();getFriendCount();return processLogs(true)}).then(initialiseCounter);EventHandler.listen('friend_added',function(client){friends[client.playerId]={avatar:client.avatar,class:client.charClass,level:client.level,name:client.pname,player_id:client.playerId,profession_id:client.professionId,subclass:client.subClass};canNextSend=null;setTimeout(updateCounter,1000);});EventHandler.listen('friend_removed',function(friendId){delete friends[friendId];canNextSend=null;setTimeout(updateCounter,1000);});EventHandler.listen(Game.sesData[sesKeys[0]].counter.key,function(amount){newLogs=true;});}
var generateSendLink=function(pid){return $('<a></a>').text(Game.sesData[getActiveSesKeys()[0]].friendsbar.label).click(function(){sendSesCurrency(pid).then(msg=>MessageSuccess(msg).show()).catch(msg=>MessageError(msg).show());$(this).parent().parent().remove();});};var generatePlayerLink=function(pid){return $('<a></a>').text(friends[pid].name).click(function(){void(PlayerProfileWindow.open(parseInt(pid)));});};var generateDeleteFriendLink=function(pid){return $('<img>').attr({'src':'https://westens.innogamescdn.com/images/icons/delete.png','title':'<span>Remove friend</span>','id':'xsht_remove_'+pid}).click(function(){new west.gui.Dialog("Remove friend","Do you really want to delete this player from the list?").setIcon(west.gui.Dialog.SYS_QUESTION).addButton("yes",function(){Ajax.remoteCall('character','cancel_friendship',{friend_id:pid},function(json){if(json["result"]){new UserMessage("Friend removed from your list.",UserMessage.TYPE_SUCCESS).show();$("div.friendData_"+pid,FriendslistWindow.DOM).remove();$('#xsht_remove_'+pid).parent().parent().remove();Chat.Friendslist.removeFriend(pid);EventHandler.signal("friend_removed",pid);}else new UserMessage("Friend could not be removed",UserMessage.TYPE_ERROR).show();});}).addButton("no").show();}).css({'cursor':'pointer','position':'relative','bottom':'1px'});};var appendPlayerToTable=function(table,pid){var pLog=playerLogs[pid];var totalAmount,logToolTip,currentText,currentDate;if(pLog===undefined){totalAmount=0;logToolTip=$('<a>').attr('title','<div>Player did not send you any currency yet.</div>').text(' ('+totalAmount+')');}else{totalAmount=pLog.total;logToolTip=$('<a>').attr('title','<div><center><b>Dates you received currency from:</b> </br>').text(' ('+totalAmount+')');for(var i=0;i<playerLogs[pid].frequency.length;i++){currentText=logToolTip.attr('title');currentDate=new Date(playerLogs[pid].frequency[i]*1000);if(i==playerLogs[pid].frequency.length-1)
logToolTip.attr('title',currentText+'<br>'+currentDate.toDateTimeStringNice()+'</center></div>');else
logToolTip.attr('title',currentText+'<br>'+currentDate.toDateTimeStringNice());}}
table.appendRow().appendToCell(-1,'remove-link',generateDeleteFriendLink(pid)).appendToCell(-1,'player-names',generatePlayerLink(pid)).appendToCell(-1,'total-received',logToolTip);if(timeUntilSesReady(pid)){var totalSec=timeUntilSesReady(pid);var hours=parseInt(totalSec / 3600)%24;var minutes=parseInt(totalSec / 60)%60;var formattedTime=$('<a>').attr('title','<div><b>Time remaining until you can send</b></div>').text(s('%1h %2m',hours,minutes));table.appendToCell(-1,'send-links',formattedTime);}else{table.appendToCell(-1,'send-links',generateSendLink(pid));}};function prettyDropTypes(key){return logsMetadata.logTypes[key]||logTypes[key]||"Unknown";}
var refreshTable=function(sortingType){processLogs(false).then(function(){var players=[];$.each(friends,function(pid){try{players.push({'id':pid,'name':friends[pid].name,'total':playerLogs[pid].total,'timeUntilReady':timeUntilSesReady(pid)});}catch(e){players.push({'id':pid,'name':friends[pid].name,'total':0,'timeUntilReady':timeUntilSesReady(pid)});}});players.sort(function(a,b){if(sortingType!=="name")
return(sortingIsAscent)?b[sortingType]-a[sortingType]:a[sortingType]-b[sortingType];else
return(sortingIsAscent)?((a.name.toLowerCase()>b.name.toLowerCase())?1:-1):((a.name.toLowerCase()<b.name.toLowerCase())?1:-1);});var friendsTable=new west.gui.Table();friendsTable.addColumn('remove-link').setId('twbf-table');var nameColHead=$("<a>").html('<img src="//westzzs.innogamescdn.com/images/icons/user.png" alt="" />&nbsp;'+'Name').click(function(){refreshTable("name");sortingIsAscent=!sortingIsAscent;});friendsTable.addColumn('player-names').appendToCell('head','player-names',nameColHead);var receivedColHead=$("<a>").text('Received').click(function(){refreshTable("total");sortingIsAscent=!sortingIsAscent;});friendsTable.addColumn('total-received').appendToCell('head','total-received',receivedColHead);var sendColHead=$("<a>").text('Send').click(function(){refreshTable("timeUntilReady");sortingIsAscent=!sortingIsAscent;});friendsTable.addColumn('send-links').appendToCell('head','send-links',sendColHead);for(var i=0;i<players.length;i++)
appendPlayerToTable(friendsTable,players[i].id);var moreData="";$.each(dropTypeLogs,function(key){moreData+='<b>'+prettyDropTypes(key)+':</b> '+dropTypeLogs[key]+', ';});$('#twbf-table').html(friendsTable.divMain);});}
function openWindow(){processLogs(false).then(function(){var players=[];$.each(friends,function(pid){try{players.push({'id':pid,'name':friends[pid].name,'total':playerLogs[pid].total,'timeUntilReady':timeUntilSesReady(pid)});}catch(e){players.push({'id':pid,'name':friends[pid].name,'total':0,'timeUntilReady':timeUntilSesReady(pid)});}});players.sort(function(a,b){return a.timeUntilReady-b.timeUntilReady;});var windowContent=new west.gui.Scrollpane();var friendsTable=new west.gui.Table();friendsTable.addColumn('remove-link').setId('twbf-table');var nameColHead=$("<a>").html('<img src="//westzzs.innogamescdn.com/images/icons/user.png" alt="" />&nbsp;'+'Name').click(function(){refreshTable("name");sortingIsAscent=!sortingIsAscent;});friendsTable.addColumn('player-names').appendToCell('head','player-names',nameColHead);var receivedColHead=$("<a>").text('Received').click(function(){refreshTable("total");sortingIsAscent=!sortingIsAscent;});friendsTable.addColumn('total-received').appendToCell('head','total-received',receivedColHead);var sendColHead=$("<a>").text('Send').click(function(){refreshTable("timeUntilReady");sortingIsAscent=!sortingIsAscent;});friendsTable.addColumn('send-links').appendToCell('head','send-links',sendColHead);for(var i=0;i<players.length;i++)
appendPlayerToTable(friendsTable,players[i].id);var moreData="";$.each(dropTypeLogs,function(key){moreData+='<b>'+prettyDropTypes(key)+':</b> '+dropTypeLogs[key]+', ';});windowContent.appendContent(friendsTable.divMain);windowContent.appendContent(moreData);wman.open('twbf','twbf','noreload').setTitle('TW Best Friends').appendToContentPane(windowContent.divMain).setMiniTitle('TW Best Friends - Sending currency made easier!').setSize('500','420');});}
var styling=$('<style></style>').text('.remove-link { width:20px; } .player-names { width:175px; } .total-received { text-align:center; width:50px; } .send-links { text-align:right; width:170px }');$('head').append(styling);function initialiseCounter(){$('.xsht_custom_unit_counter').remove();var evAvailable=$('<span></span>').attr('id','twbf_value').text(getSesReadyCount());var evLimit=$('<span></span>').attr('class','twbf_limit').text(' / '+getFriendCount()).css({'color':'lightgray','font-size':'11px'});var timeUntilCanSend=getSesSmallestTimer();var hours=parseInt(timeUntilCanSend / 3600)%24;var minutes=parseInt(timeUntilCanSend / 60)%60;var formattedTime=$('<span></span>').css({'position':'absolute','left':'3px','top':'1px','font-size':'12px'}).attr({'title':'<div><b>Time remaining until you can send</b></div>','id':'twbf_timer'}).text(' ');var evValue=$('<div></div>').attr('class','value').css({'position':'absolute','left':'32px','top':'3px','width':'105px','height':'25px','line-height':'25px','pading':'0 5px','color':'#f8c57c','font-size':'13pt','text-align':'right','user-select':'none','background':'url("https://westzzs.innogamescdn.com/images/interface/custom_unit_counter_sprite.png?2") no-repeat 0 -36px','z-index':'1'}).html(evAvailable).append(evLimit);evValue.append(formattedTime);if(timeUntilCanSend!=0){formattedTime.text(s('%1h %2m',hours,minutes));setTimeout(updateCounterTimer,(timeUntilCanSend%60+1)*1000);}
var evCounter=$('<div></div>').attr({'class':'xsht_custom_unit_counter','id':'twbf','title':'Open TW Best Friends'}).css({'position':'absolute','top':'32px','left':'50%','margin-left':'-250px','z-index':'16','width':'180px','height':'36px','text-align':'left','text-shadow':'1px 1px 1px #000','background':'url("https://westzzs.innogamescdn.com/images/interface/custom_unit_counter_sprite.png?2") no-repeat 50% 0','cursor':'pointer'}).append(evValue).click(openWindow);$("#ui_topbar").before(evCounter);}
function updateCounter(){WestUi.TopBar._redraw($("#twbf_value"),getSesReadyCount());$('.twbf_limit').text(' / '+getFriendCount());}
function updateCounterTimer(){var timeUntilCanSend=getSesSmallestTimer();var hours=parseInt(timeUntilCanSend / 3600)%24;var minutes=parseInt(timeUntilCanSend / 60)%60;if(timeUntilCanSend==0){updateCounter();$('#twbf_timer').text(' ');}else{$('#twbf_timer').text(s('%1h %2m',hours,minutes));setTimeout(updateCounterTimer,(timeUntilCanSend%60+1)*1000);}}
initialiseScript();function registerToWestApi(){scriptInfo="What are you doing here?";window.scriptyscript={script:TheWestApi.register('twbf','The West Best Friends','2',Game.version.toString(),'xShteff, Diggo11, Leones/Slygoxx','https://github.com/xShteff'),setGui:function(){this.script.setGui(scriptInfo);},init:function(){this.setGui();}};window.scriptyscript.init();}}).toString()+')()';document.head.appendChild(script);
